<?php/*  Template Name: One Page Template */get_header();?> <?php $the_query_onepage = new WP_Query('post_type=onepage&posts_per_page=20'); ?><?php if ($the_query_onepage->have_posts()) : ?>    <div id="wrapper">        <div class="container">            <div class="row">                <?php while ($the_query_onepage->have_posts()) : $the_query_onepage->the_post(); ?>                    <?php $page = get_page(get_the_ID()); ?>                    <?php $slug = $page->post_name; ?>                    <div id="<?php echo $slug ?>" class="section-main swipe-menu clearfix">                                                <?php the_content() ?>	                                                    </div>                    <?php                endwhile;                wp_reset_query();                ?>            <?php else : ?>                 <?php get_template_part('no-results', 'index'); ?>            <?php endif; ?>         </div>    </div></div><!--End #wrapper--><?phpget_footer();